import React, { useState,useEffect } from 'react'
import axios from 'axios'

function App() {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=dc5e91de9b08d11ada11c13da80edcef`;

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  function addOrdinalSuffix(day) {
    if (day >= 11 && day <= 13) {
      return `${day}th`;
    }

    switch (day % 10) {
      case 1:
        return `${day}st`;
      case 2:
        return `${day}nd`;
      case 3:
        return `${day}rd`;
      default:
        return `${day}th`;
    }
  }


  const searchLocation = (event) => {
    if (event.key == 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }


  return (
    <div className="app">
      <div className="search">
       <p1>CITY</p1>
       <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>


      <div className="container">
        <div className="top">
          <div className="location">
            <p2>{data.name}</p2>
          </div>
          <div className='time'>
            <p2>{currentDateTime.toLocaleString('en-US', { hour: '2-digit',hour24: false })}</p2>
          </div>
          <div className='temp'>
            {data.main ? <h1>{data.main.temp}Â°F</h1> : null}
          </div>
          <div className='description'>
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>

        {data.name !== undefined &&
        <div className="bottom">
          <div className="feels">
            {data.main ? <p className='bold'>{data.main.feels_like}Â°F</p> : null}
            <p>Feels Like</p>
          </div>
          <div className="humidity">
            {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
            <p>Humidity</p>
          </div>
          <div className="wind">
            {data.wind ? <p className='bold'>{data.wind.speed} MPH</p> : null}
            <p>Wind Speed</p>
          </div>
        </div>
}
      </div>
    </div>






  );
}

export default App;
