import React, { useState,useEffect } from 'react'
import axios from 'axios'

function App({ sourceTime, sourceTimeZone, targetTimeZone, targetTimeZoneOffset }) {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')
  const [convertedTime, setConvertedTime] = useState('');

  useEffect(() => {
    const targetTimeZoneOffsetMs = targetTimeZoneOffset * 60 * 1000;
    const targetTimeZone = new Date(Date.parse(sourceTime) + targetTimeZoneOffsetMs).toLocaleString('en-US', { timeZone: targetTimeZone });
    setConvertedTime(targetTimeZone);
  }, [sourceTime, targetTimeZone, targetTimeZoneOffset]);


  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=dc5e91de9b08d11ada11c13da80edcef`;

  const searchLocation = (event) => {
    if (event.key == 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }


  return (
    <div className="app">
      <div className="search">
       <p1>CITY</p1>
       <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>


      <div className="container">
        <div className="top">
          <div className="location">
            <p2>{data.name}</p2>
          </div>
          <div className='time'>
            <p2>{data.timezone}</p2>
          </div>
          <div className='temp'>
            {data.main ? <h1>{data.main.temp}°F</h1> : null}
          </div>
          <div className='description'>
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>

        {data.name !== undefined &&
        <div className="bottom">
          <div className="feels">
            {data.main ? <p className='bold'>{data.main.feels_like}°F</p> : null}
            <p>Feels Like</p>
          </div>
          <div className="humidity">
            {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
            <p>Humidity</p>
          </div>
          <div className="wind">
            {data.wind ? <p className='bold'>{data.wind.speed} MPH</p> : null}
            <p>Wind Speed</p>
          </div>
        </div>
}
      </div>
    </div>






  );
}

export default App;
